mapper.select:
SELECT COUNT(*)
FROM SUBJECT
WHERE PROF_ID=#{profId}




		AND SUB_YEAR=#{con1}


		AND SUB_SMST=#{con2}


		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_YEAR=#{con1}
		AND SUB_SMST=#{con2}


		AND SUB_YEAR=#{con1}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_SMST=#{con2}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_YEAR=#{con1}
		AND SUB_SMST=#{con2}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'

SELECT A.SUB_YEAR, A.SUB_SMST, A.SUB_CODE, B.DEPT_TITLE, A.SUB_DIVS, A.SUB_TITLE, A.SUB_CREDIT, A.SUB_TYPE, C.PROF_NAME
FROM SUBJECT A
JOIN DEPARTMENT B ON A.DEPT_CODE=B.DEPT_CODE 
JOIN PROFESSOR C ON A.PROF_ID=C.PROF_ID
WHERE A.PROF_ID=#{profId}




		AND SUB_YEAR=#{con1}


		AND SUB_SMST=#{con2}


		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_YEAR=#{con1}
		AND SUB_SMST=#{con2}


		AND SUB_YEAR=#{con1}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_SMST=#{con2}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


		AND SUB_YEAR=#{con1}
		AND SUB_SMST=#{con2}
		AND SUB_TITLE LIKE '%'||#{keyword}||'%'


ORDER BY SUB_YEAR DESC, SUB_SMST DESC

SELECT SUB_CODE, DEPT_CODE, SUB_DIVS, SUB_TITLE, SUB_CREDIT, SUB_TYPE, PROF_ID
FROM SUBJECT

WHERE PROF_ID=#{profId} AND SUB_YEAR=#{gradeYear} AND SUB_SMST=${gradeSemester}

mapper.update:
UPDATE GRADE 
SET ATTENDANCE=#{attend}, ASSIGNMENT=#{assign}, MIDEXAM=#{mid}, FINEXAM=#{fin}
WHERE STU_ID=#{stuId} AND SUB_CODE=#{subCode} 
AND GRADE_YEAR=#{gradeYear} AND GRADE_SEMESTER=${gradeSemester}

UPDATE GRADE
SET COUNT = COUNT + 1
WHERE GRADE_NO=#{gradeNo}
